import Foundation

// https://www.hackerrank.com/challenges/alternating-characters/problem?h_l=interview&playlist_slugs%5B%5D=interview-preparation-kit&playlist_slugs%5B%5D=strings

func alternatingCharacters(s: String) -> Int {
    let characterArray = Array(s)
    
    var numberOfDeletions = 0
    for i in 0..<characterArray.count {
        if i+1 >= characterArray.count { break }

        let currentCharacter = characterArray[i]
        let nextCharacter = characterArray[i + 1]

        if currentCharacter == nextCharacter {
            numberOfDeletions += 1
        }
    }
    
    return numberOfDeletions
}

let deletions = alternatingCharacters(s: "")

